import React, {useState, useEffect} from 'react';
import {Route, Switch} from 'react-router-dom';
import PirateList from '../components/pirates/PirateList';
import PirateDetail from '../components/pirates/PirateDetail';
import Request from '../helpers/request';
import PirateForm from '../components/pirates/PirateForm';
import ShipList from '../components/ships/ShipList';
import RaidList from '../components/raids/RaidList'

const ShipContainer = () => {
    const [pirates, setPirates] = useState([]);
    const [ships, setShips] = useState([]);
    const [raids, setRaids] = useState([]);

    const requestAll = function(){
      const request = new Request();
      const piratePromise = request.get('/api/pirates')
      const shipPromise = request.get('/api/ships')
      const raidPromise = request.get('/api/raids')

      Promise.all([piratePromise, shipPromise, raidPromise])
      .then((data) => {
          setPirates(data[0]);
          setShips(data[1]);
          setRaids(data[2])
      })
    }

    useEffect(()=>{
      requestAll()
    }, [])

    const findShipById = function(id){
      return ships.find((ship) => {
        return ship.id === parseInt(id);
      })
    }

    const handleDelete = function(id){
      const request = new Request();
      const url = "/api/ships/" + id
      request.delete(url)
      .then(() => window.location = "/ships")
    }

    const handlePost = function(ship){
      const request = new Request();
      request.post("/api/ships", ship)
      .then(() => window.location = '/ships')
    }


    if(!ships){
      return null
    }
    return(
        <>
        <Switch>

        <Route exact path = '/ships/new' render={(props) =>{
        return <ShipForm ships = {ships} onCreate= {handlePost}/>
        }}/>

        <Route exact path="/ships/:id" render={(props) =>{
          const id = props.match.params.id;
          const ship = findShipById(id);
          return <ShipDetail ship={ship}
          onDelete={handleDelete}
          />
        }}/>

        <Route render={() => {
          return <ShipList ships={ships}/>
        }} />

        </Switch>
        </>
    )
  }

  export default ShipContainer;
